using EvolutionModel.Model.Environment;
using EvolutionModel.Model.PhenoTypes.Digestion;
using EvolutionModel.Model.PhenoTypes.Energy_Factory;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace EvolutionModel.Model.Genotypes
{
    [Serializable]
    public class OrganismProcessor : IOrganismProcessor
    {
        private const int CARNIVORE_FREQUENCY = 5;
        private BioEvolveEnvironment environment;

        public OrganismProcessor(BioEvolveEnvironment environment)
        {
            this.environment = environment;
        }

        public Organism randomAnimal()
        {
            Animal organism = new Animal(environment);
            organism = randomizeBaseAnimalPhenotypes(organism);
            return organism;
        }

        private Animal randomizeBaseAnimalPhenotypes(Animal organism)
        {
            organism.favoredHungerThreshold = Animal.DEFAULT_FAVORED_HUNGER_THRESHOLD;
            organism.unfavoredHungerThreshold = Animal.DEFAULT_UNFAVORED_HUNGER_THRESHOLD;
            organism.reproductionThreshold = Animal.DEFAULT_REPRODUCTION_THRESHOLD;
            int randomNum = OrganismFactory.random.Next();
            if (randomNum % CARNIVORE_FREQUENCY == 0)
                organism.Digestion = new Carnivore();
            else
                organism.Digestion = new Herbivore();
            return organism;
        }

        public Organism randomPlant()
        {
            Plant organism = new Plant();
            organism = randomizeBasePlantPhenotypes(organism);
            return organism;
        }

        private Plant randomizeBasePlantPhenotypes(Plant organism)
        {
            organism.growthRate = Plant.DEFAULT_GROWTH_RATE;
            organism.growthThresholdToNutrients = Plant.DEFAULT_GROWTH_THRESHOLD;
            WaterPermeableMembrane membrane = new WaterPermeableMembrane();
            organism.NutrientAbsorbtion = membrane;
            organism.EnergyFactory = membrane;
            return organism;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
        }

        public Organism randomParasite()
        {
            Parasite organism = new Parasite();
            organism = randomizeBaseParasiticPhenotypes(organism);
            return organism;
        }

        private Parasite randomizeBaseParasiticPhenotypes(Parasite organism)
        {
            organism.Digestion = new ParasiticDigestiveSystem();
            return organism;
        }
    }
}
